$font-primary: #163950;
$sudbury-green-btn-shadow: rgba(119, 187, 177, 0.5);
$not-white: #f5f5f5;
$navy: #22577a;
$newton-blue: #567d9f;
$newton-blue-light: #7ba5c9;
$sudbury-green: #77bbb1;
$header-height: auto;

@media only screen and (min-width: 768px) {
    header {
        nav {
            div#hamburgerContainer {
                display: none;
            }

            div#dropdownContainer,
            ul#dropdownMenu {
                width: 450px;
                display: flex !important;
                flex-direction: row !important;
                justify-content: space-evenly;
                top: 0% !important;
                background-color: $navy !important;
            }
        }
    }

    main {
        width: 100vw;

        article#aboutContainer {
            display: grid;
            grid-template-columns: auto 1fr;
            grid-template-areas:
                "heading headshot"
                "moreAbout tech";

            .headshotContainer {
                grid-area: headshot;
                justify-content: space-between;
                align-items: center;
                flex-direction: column;

                img {
                    width: 235px !important;
                    margin-bottom: 2rem;
                }
            }

            #aboutMe {
                display: flex;
                flex-direction: column;
                grid-area: heading;
                align-items: center;

                h1,
                h2 {
                    line-height: 2.5rem;
                    width: 100%;
                    justify-self: center;
                }
            }

            #moreAboutMe {
                grid-area: moreAbout;
                justify-content: center;
            }

            #socialMediaIcons {
                grid-area: headshot;
                align-self: flex-end !important;
                justify-self: center;
            }

            #technologies {
                grid-area: tech;
                grid-template-areas:
                    "header header header"
                    "first first first"
                    "second second second"
                    "third third third"
                    "fourth fourth fourth" !important;
                width: 80%;
                justify-self: center;

                div.techLogo.sass {
                    grid-row: 4 !important;
                }

                div.techLogo.js {
                    grid-column: 2 !important;
                }
            }
        }

        article#projectsContainer {
            display: grid;
            grid-template-rows: 1fr 1fr 1fr;
            grid-template-columns: 1fr 1fr;
            width: 100%;
        }
    }
}
