}

* {
	box-sizing: border-box;
}

$font-primary: #163950;
$sudbury-green-btn-shadow: #77bbb180;
$not-white: #f5f5f5;
$navy: #22577a;
$newton-blue: #567d9f;
$newton-blue-light: #7ba5c9;
$sudbury-green-dark: #568a82;
$sudbury-green-light: rgba(119, 187, 177, 1);
$header-height: auto;

html {
	width: 100%;
}

body {
	background-image: linear-gradient(to bottom right, #b7d0e170, #bfe7e750, #e6f6f750, #e6f7f850, #ffffff80);
	width: 100%;
	padding: 0;
	margin: 0;
	justify-content: center;
	color: $font-primary;
}

a,
a:visited {
	text-decoration: none;
	color: inherit;
}

a:hover {
	color: inherit;
}

main {
	display: grid;
	grid-template-areas:
		"about"
		"projects"
		"blogs"
		"contact";
	padding: 3%;
	margin: 0 auto;
	justify-content: center;
	justify-self: center;
	overflow: hidden;

	.hidden {
		display: none !important;
		transition: display 3000ms;
		transition-timing-function: ease-in-out;
	}

	.visible {
		display: flex;
		transition: display 3000ms;
		transition-timing-function: ease-in-out;
	}

	.sectionDescription {
		width: 95%;
		margin: 1rem auto;
		text-align: justify;
		text-align-last: center;	
		color: $navy;
		border-radius: 0.5rem;
		padding: 0.5rem;
		font-size: 1.35rem;
	}

	span.headingLink {
		color: $sudbury-green-dark;
		font-weight: 500;
	}

	h5 span#headingResumeLink a {
		align-self: center;
		color: $newton-blue-light;
	}


	h2.sectionHeading {
		color: $newton-blue;
		font-size: 1.5rem;
		margin: 1rem auto;
		text-align: center;
	}

	p.techStack {
		color: $sudbury-green-dark;
		font-style: italic;
	}

	i#scrollArrow {
		position: fixed;
		bottom: 5px;
		right: 1px;
		color: $sudbury-green-dark;
		font-size: 0.8rem;
	}

	hr.gray {
		align-self: center;
		width: 50vw;
		border-top: 1px dashed #e5e5e5;
		margin: 1rem auto;
	}

	hr.green {
		align-self: center;
		width: 80vw;
		border-top: 1px dashed $sudbury-green-btn-shadow;
		margin: 1rem auto;
	}

	article#aboutContainer {
		grid-area: about;
		display: grid;
		flex-direction: column;
		line-height: 1.25rem;
		width: 100%;
		margin: 0 auto;
		text-align: center;

		#welcome {
			color: $newton-blue-light;
		}

		h1 {
			font-size: 1.75rem;
			line-height: 2rem;
			color: $navy;
		}

		h2 {
			font-size: 1.5rem;
			margin-bottom: 1em;
			line-height: 2rem;

		}

		h3 {
			color: $newton-blue;
			font-style: italic;
		}

		h4 {
			font-weight: normal;
			font-size: 1.25rem;
			grid-area: header;
			justify-self: center;
			font-style: normal;
			margin-bottom: 1rem;
			line-height: 1.5rem;
			color: $navy;
		}


		div#technologies {
			display: grid;
			grid-template-areas:
				"header header header header"
				"top top top top"
				"middle middle middle middle"
				"bottom bottom bottom bottom";
			align-self: center;
			justify-self: center;
			grid-gap: 0.1rem;
			width: 95%;
			margin: 0 auto;

			h3 {
				font-size: 1.25rem;
				grid-area: header;
				justify-self: center;
				font-style: normal;
				margin-bottom: 1rem;
			}

			img {
				width: 1.2rem;
			}

			svg {
				width: 1.3rem;
				height: 1.3rem;
			}

			i {
				font-size: 1.19rem;
			}

			div.techLogo.js {
				grid-area: bottom;
				grid-column: 3;
			}

			div.techLogo.sass {
				grid-area: bottom;
				grid-column: 2;
			}

			div.techLogo.figma {
				grid-area: bottom;
				grid-column: 4;
			}

			div.techLogo.htmlCss {
				grid-area: bottom;
				grid-column: 1;
			}

			div {
				display: flex;
				flex-direction: column;
				align-items: center;
				justify-content: center;

				p {
					font-size: 16px;
					color: $sudbury-green-dark;
				}
			}
		}

		div.headshotContainer {
			display: flex;
			align-self: center;
			margin-bottom: 1rem;
			margin-top: 1rem;
			flex-direction: column;
			align-items: center;

